// 5. Event Loop

// ------------------------------------------------------------------------> 5.1
function printing() {
   console.log(1);     // 1 es primero 1째
   setTimeout(function () {
      console.log(2);      // 2 es cuarto 4째
   }, 1000);
   setTimeout(function () {
      console.log(3);      // 3 es tercero 3째
   }, 0);
   console.log(4);     // 4 es segundo 2째
}

printing(); // 1, 4, 3, 2



